-- Create a function to parse XML without DTD validation\nCREATE OR REPLACE FUNCTION parse_xml_without_dtd(xml_content CLOB) RETURN XMLTYPE IS\n  l_parser DBMS_XMLPARSER.Parser;\n  l_doc    DBMS_XMLDOM.DOMDocument;\n  l_xml    XMLTYPE;\nBEGIN\n  l_parser := DBMS_XMLPARSER.newParser;\n  DBMS_XMLPARSER.setValidateDTD(l_parser, FALSE);\n  DBMS_XMLPARSER.parseClob(l_parser, xml_content);\n  l_doc := DBMS_XMLPARSER.getDocument(l_parser);\n  l_xml := XMLTYPE(l_doc);\n  DBMS_XMLPARSER.freeParser(l_parser);\n  RETURN l_xml;\nEND;\n/\n\n-- Use the function in your query\nSELECT extract(parse_xml_without_dtd(doc.payload), '/SHOW_SHIPMENT_005/DATAAREA/SHOW_SHIPMENT/SHIPMENT/SHIPITEM/DOCUMNTREF/DOCUMENTID')\nFROM your_table doc;