CREATE OR REPLACE PROCEDURE proc1 (p_table IN t_rec_obj_table) IS\n    BEGIN\n        FOR i IN 1 .. p_table.COUNT LOOP\n            DBMS_OUTPUT.PUT_LINE('ID: ' || p_table(i).id);\n        END LOOP;\n    END;\n    /\n\n    CREATE OR REPLACE PROCEDURE proc2 IS\n        v_table t_rec_obj_table;\n    BEGIN\n        v_table := t_rec_obj_table(t_rec_obj(1), t_rec_obj(2));\n        proc1(v_table);\n    END;\n    /