WITH Data AS (\n       SELECT * FROM YourStoredProcedure\n   ),\n   PaddedData AS (\n       SELECT ROW_NUMBER() OVER (ORDER BY (SELECT NULL)) AS RowNum, *\n       FROM Data\n   ),\n   TenRows AS (\n       SELECT RowNum, Column1, Column2, Column3 -- Add all your columns here\n       FROM PaddedData\n       UNION ALL\n       SELECT RowNum, NULL AS Column1, NULL AS Column2, NULL AS Column3 -- Add all your columns here\n       FROM (SELECT TOP (10 - (SELECT COUNT(*) FROM Data)) ROW_NUMBER() OVER (ORDER BY (SELECT NULL)) AS RowNum\n             FROM sys.objects) AS Padding\n   )\n   SELECT *\n   FROM TenRows\n   ORDER BY RowNum