CREATE VIEW user_activities AS\nSELECT \n    'comment' AS activity_type, \n    comments.id AS reference_id, \n    comments.user_id, \n    comments.content AS activity_content, \n    comments.created_at \nFROM comments\nUNION ALL\nSELECT \n    'page_update' AS activity_type, \n    page_updates.id AS reference_id, \n    page_updates.user_id, \n    page_updates.description AS activity_content, \n    page_updates.created_at \nFROM page_updates\nUNION ALL\nSELECT \n    'subscription' AS activity_type, \n    subscriptions.id AS reference_id, \n    subscriptions.user_id, \n    CONCAT('Subscribed to: ', subscriptions.plan_name) AS activity_content, \n    subscriptions.created_at \nFROM subscriptions;