CREATE FUNCTION dbo.RemoveNonNumeric\n(\n    @input VARCHAR(255)\n)\nRETURNS VARCHAR(255)\nAS\nBEGIN\n    DECLARE @output VARCHAR(255) = ''\n    DECLARE @i INT = 1\n    DECLARE @char CHAR(1)\n\n    WHILE @i <= LEN(@input)\n    BEGIN\n        SET @char = SUBSTRING(@input, @i, 1)\n        IF @char LIKE '[0-9]'\n            SET @output = @output + @char\n        SET @i = @i + 1\n    END\n\n    RETURN @output\nEND\nGO\n\n-- Usage example\nDECLARE @Num VARCHAR(20)\nSET @Num = ' + (82) 468 6152 '\n\nSELECT dbo.RemoveNonNumeric(@Num) AS CleanedNumber