SELECT \n  Destaque.destaque, \n  Noticia.id, \n  Noticia.antetitulo, \n  Noticia.titulo, \n  Noticia.lead, \n  Noticia.legenda, \n  Noticia.publicacao, \n  Seccao.descricao, \n  Album.pasta,\n  Foto.ficheiro, \n  Foto.descricao, \n  Cronista.nome, \n  Cronista.profissao, \n  Cronista.ficheiro,\n  MAX(CASE WHEN AudioFile.mimeType != '' THEN AudioFile.* END) AS AudioFile,\n  MAX(CASE WHEN AudioCollection.id IS NOT NULL THEN AudioCollection.* END) AS AudioCollection,\n  MAX(CASE WHEN VideoFile.mimeType = '' THEN VideoFile.* END) AS VideoFile,\n  MAX(CASE WHEN VideoCollection.id IS NOT NULL THEN VideoCollection.* END) AS VideoCollection\nFROM \n  nt_highlights AS Destaque\n  LEFT JOIN nt_noticias  AS Noticia         ON Destaque.noticia_id = Noticia.id\n  LEFT JOIN mm_fotos     AS Foto            ON Noticia.foto_id = Foto.id\n  LEFT JOIN nt_temas     AS Seccao          ON Noticia.tema_id = Seccao.id\n  LEFT JOIN mm_albuns    AS Album           ON Foto.album_id = Album.id\n  LEFT JOIN nt_cronistas AS Cronista        ON Cronista.id = Noticia.cronista_id  \n  LEFT JOIN ntNoticias_mmFiles AS Rel       ON Rel.noticia_id = Noticia.id\n  LEFT JOIN mm_files     AS AudioFile       ON AudioFile.id = Rel.file_id AND AudioFile.mimeType != ''\n  LEFT JOIN mm_files     AS VideoFile       ON VideoFile.id = Rel.file_id AND VideoFile.mimeType = ''\n  LEFT JOIN mm_coleccoes AS AudioCollection ON AudioFile.coleccao_id = AudioCollection.id        \n  LEFT JOIN mm_coleccoes AS VideoCollection ON VideoFile.coleccao_id = VideoCollection.id\nWHERE \n  Destaque.area_id = 1\n  AND Noticia.paraPublicacao = 1 \n  AND Noticia.publicacao <= NOW()   \nGROUP BY \n  Destaque.destaque, \n  Noticia.id, \n  Noticia.antetitulo, \n  Noticia.titulo, \n  Noticia.lead, \n  Noticia.legenda, \n  Noticia.publicacao, \n  Seccao.descricao, \n  Album.pasta,\n  Foto.ficheiro, \n  Foto.descricao, \n  Cronista.nome, \n  Cronista.profissao, \n  Cronista.ficheiro\nORDER BY \n  Destaque.destaque;