WITH DateSequences AS (\n    SELECT \n        site_id,\n        start_date,\n        end_date,\n        ROW_NUMBER() OVER(PARTITION BY site_id ORDER BY start_date) AS row_num,\n        DATEADD(day, -ROW_NUMBER() OVER(PARTITION BY site_id ORDER BY start_date), start_date) AS grp\n    FROM \n        your_table_name\n),\nContiguousGroups AS (\n    SELECT \n        site_id,\n        MIN(start_date) AS contiguous_start_date,\n        MAX(end_date) AS contiguous_end_date,\n        COUNT(*) AS num_days\n    FROM \n        DateSequences\n    GROUP BY \n        site_id, grp\n)\nSELECT \n    site_id,\n    contiguous_start_date,\n    contiguous_end_date\nFROM \n    ContiguousGroups\nWHERE \n    num_days >= 15\nORDER BY \n    site_id, contiguous_start_date;