DECLARE @id int;\nDECLARE @countVal int;\nDECLARE @sql nvarchar(max);\n\nSET @id = 1000;\n\n-- Build the SQL string with the parameter value\nSET @sql = N'SELECT COUNT(*) FROM owner.myTable WHERE id = ' + CAST(@id AS nvarchar(10));\n\n-- Create a table to hold the result\nCREATE TABLE #ResultTable (CountVal int);\n\n-- Insert the result of the execution into the temporary table\nINSERT INTO #ResultTable\nEXEC (@sql) AT oracleServer;\n\n-- Retrieve the count value from the temporary table\nSELECT @countVal = CountVal FROM #ResultTable;\n\n-- Drop the temporary table\nDROP TABLE #ResultTable;\n\n-- Output the count value\nSELECT @countVal AS CountValue;