-- Backup the database on the source server\nBACKUP DATABASE YourDatabaseName \nTO DISK = 'C:\Path\To\Backup.bak';\n\n-- Copy the backup file to the destination server\n\n-- Restore the database on the destination server\nRESTORE DATABASE YourDatabaseName \nFROM DISK = 'C:\Path\To\Backup.bak'\nWITH MOVE 'YourDatabaseName_Data' TO 'C:\Path\To\YourDatabase.mdf',\n     MOVE 'YourDatabaseName_Log' TO 'C:\Path\To\YourDatabase_log.ldf';