-- First create the blocks table without the foreign key constraint\nCREATE TABLE IF NOT EXISTS blocks(\n    id INT UNSIGNED NOT NULL AUTO_INCREMENT,\n    title VARCHAR(100),\n    defaultpage INT UNSIGNED NOT NULL,\n    PRIMARY KEY(id)\n) ENGINE=InnoDB;\n\n-- Then create the pages table without the foreign key constraint\nCREATE TABLE IF NOT EXISTS pages(\n    pageID INT UNSIGNED NOT NULL AUTO_INCREMENT,\n    title VARCHAR(50) NOT NULL,\n    content BLOB,\n    blockid INT UNSIGNED NOT NULL,\n    PRIMARY KEY(pageID)\n) ENGINE=InnoDB;\n\n-- Now add the foreign key constraints\nALTER TABLE blocks\nADD CONSTRAINT fk_blocks_defaultpage\nFOREIGN KEY (defaultpage) REFERENCES pages(pageID);\n\nALTER TABLE pages\nADD CONSTRAINT fk_pages_blockid\nFOREIGN KEY (blockid) REFERENCES blocks(id);