CREATE OR REPLACE FUNCTION replace_special_chars(p_input IN VARCHAR2)\nRETURN VARCHAR2 IS\n  v_output VARCHAR2(4000);\nBEGIN\n  v_output := p_input;\n  \n  FOR i IN 0..31 LOOP\n    CASE i\n      WHEN 10 THEN v_output := REPLACE(v_output, CHR(i), '\n');\n      WHEN 13 THEN v_output := REPLACE(v_output, CHR(i), '\n');\n      WHEN 9  THEN v_output := REPLACE(v_output, CHR(i), '\t');\n      ELSE\n        v_output := REPLACE(v_output, CHR(i), '\x' || LPAD(TO_CHAR(i, 'XX'), 2, '0'));\n    END CASE;\n  END LOOP;\n  \n  RETURN v_output;\nEND;\n/