-- Assuming for each submitted answer you have: question_id and description (answer text)\n\n-- Example pseudocode in a server-side language:\n\nfor each submitted_answer in submitted_answers:\n    if submitted_answer.question_id exists in existing_answers:\n        -- Update existing answer\n        UPDATE answers\n        SET description = submitted_answer.description\n        WHERE question_id = submitted_answer.question_id;\n    else:\n        -- Insert new answer\n        INSERT INTO answers (question_id, description)\n        VALUES (submitted_answer.question_id, submitted_answer.description);