CREATE OR REPLACE TRIGGER trg_YourTable_AUD\n   AFTER INSERT OR UPDATE OR DELETE ON YourTable\n   FOR EACH ROW\n   DECLARE\n       v_old_value CLOB;\n       v_new_value CLOB;\n   BEGIN\n       IF INSERTING THEN\n           v_new_value := json_object('column1' VALUE :NEW.column1, 'column2' VALUE :NEW.column2);\n           INSERT INTO ChangeHistory (TableName, OperationType, ChangedBy, PrimaryKeyValue, NewValue)\n           VALUES ('YourTable', 'INSERT', USER, :NEW.PrimaryKeyColumn, v_new_value);\n       ELSIF UPDATING THEN\n           v_old_value := json_object('column1' VALUE :OLD.column1, 'column2' VALUE :OLD.column2);\n           v_new_value := json_object('column1' VALUE :NEW.column1, 'column2' VALUE :NEW.column2);\n           INSERT INTO ChangeHistory (TableName, OperationType, ChangedBy, PrimaryKeyValue, OldValue, NewValue)\n           VALUES ('YourTable', 'UPDATE', USER, :NEW.PrimaryKeyColumn, v_old_value, v_new_value);\n       ELSIF DELETING THEN\n           v_old_value := json_object('column1' VALUE :OLD.column1, 'column2' VALUE :OLD.column2);\n           INSERT INTO ChangeHistory (TableName, OperationType, ChangedBy, PrimaryKeyValue, OldValue)\n           VALUES ('YourTable', 'DELETE', USER, :OLD.PrimaryKeyColumn, v_old_value);\n       END IF;\n   END;