CREATE PROCEDURE GetOrCreateTagID\n    @TagName VARCHAR(50),\n    @TagID INT OUTPUT\nAS\nBEGIN\n    SET NOCOUNT ON;\n\n    -- Check if the TagName exists\n    SELECT @TagID = TagID\n    FROM tbrm_Tags\n    WHERE TagName = @TagName;\n\n    -- If TagID is NULL, it means the TagName does not exist\n    IF @TagID IS NULL\n    BEGIN\n        -- Insert the new TagName\n        INSERT INTO tbrm_Tags (TagName)\n        VALUES (@TagName);\n\n        -- Get the TagID of the newly inserted TagName\n        SET @TagID = SCOPE_IDENTITY();\n    END\nEND;