-- Parent table\nCREATE TABLE array_parent (\n    array_id NUMBER PRIMARY KEY,\n    description VARCHAR2(100) -- Any additional metadata\n);\n\n-- Child table\nCREATE TABLE array_element (\n    element_id NUMBER PRIMARY KEY,\n    array_id NUMBER,\n    element_index NUMBER,\n    element_value VARCHAR2(4000), -- Adjust the data type as needed\n    CONSTRAINT fk_array FOREIGN KEY (array_id) REFERENCES array_parent(array_id)\n);\n\n-- Create indexes for efficient retrieval\nCREATE INDEX idx_array_element ON array_element(array_id, element_index);