DECLARE @job_name NVARCHAR(128) = 'YourJobName'\n\nIF EXISTS (\n    SELECT 1\n    FROM msdb.dbo.sysjobs sj\n    JOIN msdb.dbo.sysjobactivity sja ON sj.job_id = sja.job_id\n    WHERE sj.name = @job_name\n    AND sja.run_requested_date IS NOT NULL\n    AND sja.stop_execution_date IS NULL\n)\nBEGIN\n    PRINT 'Job is already running. Exiting.'\n    RETURN\nEND\n\n-- Your job steps go here