CREATE PROCEDURE dbo.MyStoredProcedure\n    @JsonParams NVARCHAR(MAX)\nAS\nBEGIN\n    DECLARE @ParsedParams TABLE (Param1 INT, Param2 NVARCHAR(50));\n    \n    INSERT INTO @ParsedParams\n    SELECT \n        JSON_VALUE(value, '$.Param1') AS Param1,\n        JSON_VALUE(value, '$.Param2') AS Param2\n        -- Extract other parameters\n    FROM OPENJSON(@JsonParams) \n    WITH (value NVARCHAR(MAX) 'strict $.');\n\n    -- Access the parameters from @ParsedParams\n    SELECT * FROM @ParsedParams;\n\n    -- Add your logic here\n\nEND;