CREATE TABLE ChangeHistory (\n       HistoryID NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,\n       TableName VARCHAR2(255),\n       OperationType VARCHAR2(10), -- 'INSERT', 'UPDATE', 'DELETE'\n       ChangedBy VARCHAR2(255),\n       ChangeDate TIMESTAMP DEFAULT CURRENT_TIMESTAMP,\n       PrimaryKeyValue VARCHAR2(255), -- Store primary key of the changed row\n       OldValue CLOB, -- Store old values in case of update/delete\n       NewValue CLOB  -- Store new values in case of insert/update\n   );