-- Variables to hold the category IDs\nDECLARE @fromCategoryID INT = 1;\nDECLARE @toCategoryID INT = 2;\n\n-- Temporary table to hold the mapping of old QuestionID to new QuestionID\nCREATE TABLE #QuestionMapping (\n    OldQuestionID INT,\n    NewQuestionID INT\n);\n\n-- Step 1: Copy Questions from the source category to the destination category\nINSERT INTO Questions (Question, CategoryID)\nOUTPUT inserted.QuestionID, src.QuestionID INTO #QuestionMapping (NewQuestionID, OldQuestionID)\nSELECT Question, @toCategoryID\nFROM Questions\nWHERE CategoryID = @fromCategoryID;\n\n-- Step 2: Copy Selections based on the mapping of QuestionIDs\nINSERT INTO Selections (Selection, QuestionID)\nSELECT s.Selection, qm.NewQuestionID\nFROM Selections s\nJOIN #QuestionMapping qm ON s.QuestionID = qm.OldQuestionID;\n\n-- Cleanup\nDROP TABLE #QuestionMapping;