SELECT \n    j.dtmInvoicedOn, \n    j.strJobKey, \n    c.strCustName, \n    SUM(CASE WHEN r.strTransType = 'credit' THEN r.dblTransactionAmount ELSE 0 END) AS credit,\n    SUM(CASE WHEN r.strTransType = 'debit' THEN r.dblTransactionAmount ELSE 0 END) AS debit\nFROM \n    tbljobs AS j\nINNER JOIN \n    tblreceivledger AS r ON j.strJobKey = r.strJobKey \nINNER JOIN \n    tblcustomers AS c ON j.intCustomerID = c.intCustomerID \nWHERE \n    c.strCustName = 'Acme Runners Inc'\nGROUP BY \n    j.dtmInvoicedOn, j.strJobKey, c.strCustName\nORDER BY \n    j.dtmInvoicedOn, j.strJobKey;