-- Declare the variable\nDECLARE @bob AS varchar(50);\nSET @bob = 'SweetDB';\n\n-- Use dynamic SQL to switch databases and perform actions\nDECLARE @sql NVARCHAR(MAX);\n\n-- Switch to the database\nSET @sql = N'USE ' + QUOTENAME(@bob) + ';';\nEXEC sp_executesql @sql;\n\n-- Insert into the table\nSET @sql = N'INSERT INTO ' + QUOTENAME(@bob) + '.dbo.ProjectVersion (DB_Name, Script) VALUES (@bob, ''1.2'');';\nEXEC sp_executesql @sql, N'@bob AS varchar(50)', @bob;