SET LONG 10000\nSET PAGESIZE 0\nSET LINESIZE 1000\nSET FEEDBACK OFF\nSET ECHO OFF\n\nSPOOL your_output_file.sql\n\n-- Extract tables\nSELECT DBMS_METADATA.GET_DDL('TABLE', table_name, 'YOUR_SCHEMA') || ';'\nFROM all_tables\nWHERE owner = 'YOUR_SCHEMA';\n\n-- Extract indexes\nSELECT DBMS_METADATA.GET_DDL('INDEX', index_name, 'YOUR_SCHEMA') || ';'\nFROM all_indexes\nWHERE owner = 'YOUR_SCHEMA';\n\n-- Extract views\nSELECT DBMS_METADATA.GET_DDL('VIEW', view_name, 'YOUR_SCHEMA') || ';'\nFROM all_views\nWHERE owner = 'YOUR_SCHEMA';\n\n-- Extract sequences\nSELECT DBMS_METADATA.GET_DDL('SEQUENCE', sequence_name, 'YOUR_SCHEMA') || ';'\nFROM all_sequences\nWHERE owner = 'YOUR_SCHEMA';\n\n-- Extract other objects as needed...\n\nSPOOL OFF