-- MySQL Federated Table Example\n   CREATE SERVER postgres_server\n   FOREIGN DATA WRAPPER mysql_fdw\n   OPTIONS (host 'postgres_host', port '5432', dbname 'postgres_db', user 'user', password 'password');\n\n   CREATE TABLE federated_table (\n       id INT,\n       name VARCHAR(255),\n       email VARCHAR(255)\n   )\n   ENGINE=FEDERATED\n   CONNECTION='postgres_server/standardized_view';