CREATE OR REPLACE PROCEDURE ValidateParameters (\n    p_dateStart IN DATE,\n    p_dateEnd IN DATE,\n    p_FooParm IN NUMBER,\n    p_CodeStart IN NUMBER,\n    p_CodeEnd IN NUMBER,\n    p_errorMessage OUT VARCHAR2\n) AS\n    v_dateDiff NUMBER;\nBEGIN\n    -- Initialize the error message\n    p_errorMessage := NULL;\n\n    -- Check the date range\n    v_dateDiff := MONTHS_BETWEEN(p_dateEnd, p_dateStart);\n    IF v_dateDiff > 12 OR v_dateDiff <= 0 THEN\n        p_errorMessage := 'Date range must be greater than 0 and less than or equal to 1 year.';\n        RETURN;\n    END IF;\n\n    -- Check the FooParm or Codes conditions\n    IF (p_FooParm IS NULL OR p_FooParm = 0) THEN\n        IF (p_CodeStart IS NULL OR p_CodeEnd IS NULL OR (p_CodeEnd - p_CodeStart) >= 1001) THEN\n            p_errorMessage := 'Either FooParm must be supplied or CodeStart and CodeEnd must be supplied with a difference less than 1001.';\n            RETURN;\n        END IF;\n    END IF;\nEND ValidateParameters;\n/