CREATE TABLE StationTbl (\n    StnCode VARCHAR(10) PRIMARY KEY,\n    StnName VARCHAR(100),\n    StnCity VARCHAR(100)\n);\n\nCREATE TABLE TrainTbl (\n    TrnNumber VARCHAR(10) PRIMARY KEY,\n    TrnName VARCHAR(100),\n    SourceStn VARCHAR(10),\n    DestStn VARCHAR(10),\n    DaysofWeek VARCHAR(7),\n    FOREIGN KEY (SourceStn) REFERENCES StationTbl(StnCode),\n    FOREIGN KEY (DestStn) REFERENCES StationTbl(StnCode)\n);\n\nCREATE TABLE TrainHopTbl (\n    TrnNumber VARCHAR(10),\n    StationCode VARCHAR(10),\n    ArrTime TIME,\n    DepTime TIME,\n    HopIndex INT,\n    PRIMARY KEY (TrnNumber, StationCode),\n    FOREIGN KEY (TrnNumber) REFERENCES TrainTbl(TrnNumber),\n    FOREIGN KEY (StationCode) REFERENCES StationTbl(StnCode)\n);