-- Replace 'YourTableName' with the name of your table\nDECLARE @TableName NVARCHAR(MAX) = 'YourTableName';\nDECLARE @SchemaName NVARCHAR(MAX) = 'YourSchemaName'; -- Replace with the schema name if necessary\n\n-- Find all stored procedures that reference the table\nDECLARE @sql NVARCHAR(MAX) = N'SELECT DISTINCT OBJECT_NAME(object_id) AS ProcedureName\n                                FROM sys.sql_expression_dependencies\n                                WHERE referenced_entity_name = @TableName\n                                AND referenced_schema_name = @SchemaName\n                                AND OBJECTPROPERTY(object_id, ''IsProcedure'') = 1';\n\nDECLARE @ProcName NVARCHAR(MAX);\n\nDECLARE ProcCursor CURSOR FOR\nEXEC sp_executesql @sql, N'@TableName NVARCHAR(MAX), @SchemaName NVARCHAR(MAX)', @TableName, @SchemaName;\n\nOPEN ProcCursor;\nFETCH NEXT FROM ProcCursor INTO @ProcName;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    -- Recompile the found stored procedures\n    EXEC sp_recompile @ProcName;\n    FETCH NEXT FROM ProcCursor INTO @ProcName;\nEND\n\nCLOSE ProcCursor;\nDEALLOCATE ProcCursor;