-- On your SQL Server Standard instance (Publisher)\n-- Example: Setting up a publication\nUSE [your_database]\nEXEC sp_replicationdboption @dbname = N'your_database', @optname = N'publish', @value = N'true'\nEXEC sp_addpublication @publication = N'YourPublication', @status = N'active', @allow_push = N'true'\n\n-- Example: Adding an article to the publication\nEXEC sp_addarticle @publication = N'YourPublication', @article = N'YourTable', @source_object = N'YourTable'\n\n-- On your SQL Server Express instance (Subscriber)\n-- Example: Setting up a subscription\nEXEC sp_addsubscription @publication = N'YourPublication', @subscriber = N'YourSubscriberServer', @destination_db = N'your_database'