USE msdb;\n   GO\n   -- Create a credential for the proxy\n   EXEC dbo.sp_add_proxy\n       @proxy_name = 'SSISProxy',\n       @credential_name = 'YourCredentialName',\n       @enabled = 1;\n   \n   -- Grant the proxy access to the subsystem\n   EXEC dbo.sp_grant_proxy_to_subsystem\n       @proxy_name = 'SSISProxy',\n       @subsystem_id = 11;  -- SSIS Package Execution\n   GO\n\n   -- Set the job step to use the proxy\n   EXEC dbo.sp_update_jobstep\n       @job_name = 'YourJobName',\n       @step_id = 1,\n       @proxy_name = 'SSISProxy';\n   GO