CREATE TABLE POSTS (\n    id INT PRIMARY KEY,\n    content TEXT NOT NULL,\n    -- other post-related columns\n);\n\nCREATE TABLE CATEGORIES (\n    id INT PRIMARY KEY,\n    name VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE TOPICS (\n    id INT PRIMARY KEY,\n    name VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE POST_RELATIONS (\n    post_id INT,\n    related_id INT,\n    related_type VARCHAR(50),\n    PRIMARY KEY (post_id, related_id, related_type),\n    FOREIGN KEY (post_id) REFERENCES POSTS(id)\n    -- You can add additional foreign key constraints or checks if needed\n);