CREATE PROCEDURE [dbo].[pGetTask]\n    @showCompletedTasks bit = 1,\n    @showInProgressTasks bit = 1,\n    @taskID int = null,\n    @projectID int = null,\n    @applicationID int = null,\n    @clientID int = null\nAS\nBEGIN\n    DECLARE @sql NVARCHAR(MAX)\n    DECLARE @params NVARCHAR(MAX)\n\n    SET @sql = '\n        SELECT /* Your columns here */\n        FROM /* Your tables here */\n        WHERE 1=1'\n    \n    IF @clientID IS NOT NULL\n        SET @sql = @sql + ' AND a.clientID = @clientID'\n    \n    IF @applicationID IS NOT NULL\n        SET @sql = @sql + ' AND a.applicationID = @applicationID'\n    \n    IF @projectID IS NOT NULL\n        SET @sql = @sql + ' AND p.projectID = @projectID'\n    \n    IF @taskID IS NOT NULL\n        SET @sql = @sql + ' AND t.taskID = @taskID'\n    \n    IF @showCompletedTasks = 0\n        SET @sql = @sql + ' AND curr.complete = 0'\n    \n    IF @showInProgressTasks = 0\n        SET @sql = @sql + ' AND curr.complete = 1'\n    \n    SET @params = '\n        @clientID INT,\n        @applicationID INT,\n        @projectID INT,\n        @taskID INT'\n    \n    EXEC sp_executesql @sql, @params, \n        @clientID = @clientID,\n        @applicationID = @applicationID,\n        @projectID = @projectID,\n        @taskID = @taskID\nEND