-- Table to store different product groups\nCREATE TABLE ProductGroup (\n    GroupID INT PRIMARY KEY,\n    GroupName VARCHAR(255) NOT NULL\n);\n\n-- Table to store basic product information\nCREATE TABLE Product (\n    ProductID INT PRIMARY KEY,\n    Name VARCHAR(255) NOT NULL,\n    GroupID INT,\n    FOREIGN KEY (GroupID) REFERENCES ProductGroup(GroupID)\n);\n\n-- Table to define custom attributes for each product group\nCREATE TABLE Attribute (\n    AttributeID INT PRIMARY KEY,\n    GroupID INT,\n    AttributeName VARCHAR(255) NOT NULL,\n    DataType VARCHAR(50) NOT NULL, -- e.g., 'INT', 'VARCHAR', 'FLOAT'\n    FOREIGN KEY (GroupID) REFERENCES ProductGroup(GroupID)\n);\n\n-- Table to store attribute values for each product\nCREATE TABLE ProductAttribute (\n    ProductID INT,\n    AttributeID INT,\n    Value VARCHAR(255), -- Store all values as string and convert as needed\n    PRIMARY KEY (ProductID, AttributeID),\n    FOREIGN KEY (ProductID) REFERENCES Product(ProductID),\n    FOREIGN KEY (AttributeID) REFERENCES Attribute(AttributeID)\n);