DECLARE @SearchValue NVARCHAR(255)\nSET @SearchValue = 'YourSearchValue' -- Replace with the value you are searching for\n\nDECLARE @SQL NVARCHAR(MAX)\nSET @SQL = ''\n\nSELECT @SQL = @SQL + \n    'IF EXISTS (SELECT 1 FROM ' + TABLE_SCHEMA + '.' + TABLE_NAME + \n    ' WHERE ' + COLUMN_NAME + ' LIKE ''%' + @SearchValue + '%'')\n    BEGIN\n        PRINT ''' + TABLE_SCHEMA + '.' + TABLE_NAME + '''\n    END\n    ' + CHAR(13)\nFROM INFORMATION_SCHEMA.COLUMNS\nWHERE DATA_TYPE IN ('char', 'varchar', 'nchar', 'nvarchar')\n\n-- Execute the dynamically generated SQL\nEXEC sp_executesql @SQL