-- Access Sub-query\n     SELECT * FROM Orders WHERE OrderID IN (SELECT OrderID FROM OrderDetails WHERE Quantity > 10);\n\n     -- T-SQL Equivalent using JOIN\n     SELECT o.* FROM Orders o\n     JOIN OrderDetails od ON o.OrderID = od.OrderID\n     WHERE od.Quantity > 10;\n\n     -- T-SQL Equivalent using CTE\n     WITH OrderCTE AS (\n         SELECT OrderID FROM OrderDetails WHERE Quantity > 10\n     )\n     SELECT o.* FROM Orders o\n     JOIN OrderCTE oc ON o.OrderID = oc.OrderID;