CREATE OR REPLACE FUNCTION get_array_element(p_array_id NUMBER, p_index NUMBER) \nRETURN VARCHAR2 IS\n    v_element_value VARCHAR2(4000);\nBEGIN\n    SELECT element_value\n    INTO v_element_value\n    FROM array_element\n    WHERE array_id = p_array_id\n    AND element_index = p_index;\n    \n    RETURN v_element_value;\nEXCEPTION\n    WHEN NO_DATA_FOUND THEN\n        RETURN NULL;\nEND;\n/