select\n   u.name,\n   o.name,\n   case (o.type) when 'S' then 'SYSTEM TABLE' else 'TABLE' end as type_desc,\n   (select convert(varchar(8000), value) \n    from ::fn_listextendedproperty(NULL, 'user', u.name, 'table', o.name, null, null) \n    where name = 'MS_Description') as coln\nfrom\n   sys.objects o\n   join sys.schemas u on (u.schema_id = o.schema_id)\nwhere\n   o.type in ('U', 'S')\n   and u.name = 'something here'\norder by 1, 2