-- Entity table\nCREATE TABLE Entity (\n  id INT PRIMARY KEY,\n  name VARCHAR(100)\n);\n\n-- Attribute table\nCREATE TABLE Attribute (\n  id INT PRIMARY KEY,\n  name VARCHAR(100)\n);\n\n-- Value table\nCREATE TABLE Value (\n  entity_id INT,\n  attribute_id INT,\n  value TEXT,\n  FOREIGN KEY (entity_id) REFERENCES Entity(id),\n  FOREIGN KEY (attribute_id) REFERENCES Attribute(id)\n);