-- Encrypt existing data\nDECLARE @plainData VARCHAR(8000), @encryptedData VARBINARY(8000)\nDECLARE cur CURSOR FOR SELECT PlainColumn FROM YourTable\nOPEN cur\nFETCH NEXT FROM cur INTO @plainData\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    EXEC xp_crypt_encrypt 'YourSecretKey', @plainData, @encryptedData OUTPUT\n    UPDATE YourTable SET EncryptedColumn = @encryptedData WHERE CURRENT OF cur\n    FETCH NEXT FROM cur INTO @plainData\nEND\nCLOSE cur\nDEALLOCATE cur