SELECT \n    CASE \n        WHEN u.access_all_accounts THEN 'Access Granted'\n        WHEN EXISTS (SELECT 1 FROM user_accounts ua WHERE ua.user_id = u.user_id AND ua.account_id = a.account_id) THEN 'Access Granted'\n        ELSE 'Access Denied'\n    END AS access_status\nFROM \n    users u\nJOIN \n    accounts a ON a.institution_id = u.institution_id\nWHERE \n    u.user_id = ? AND a.account_id = ?;