-- Create the status_updates table\nCREATE TABLE status_updates (\n    id SERIAL PRIMARY KEY,\n    user_who_posted VARCHAR(255) NOT NULL,\n    update_text TEXT NOT NULL,\n    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP\n);\n\n-- Index on user_who_posted for efficient querying\nCREATE INDEX idx_user_who_posted ON status_updates(user_who_posted);\n\n-- Partition by month for better manageability\nCREATE TABLE status_updates_2023_10 PARTITION OF status_updates\n    FOR VALUES FROM ('2023-10-01') TO ('2023-11-01');