DECLARE @tableName NVARCHAR(255)\nDECLARE @sql NVARCHAR(MAX)\n\n-- Cursor to loop through each table in the linked server\nDECLARE table_cursor CURSOR FOR\nSELECT table_name\nFROM [linkedserver].[database].INFORMATION_SCHEMA.TABLES\nWHERE table_name LIKE 'text%'\n\nOPEN table_cursor\nFETCH NEXT FROM table_cursor INTO @tableName\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @sql = '\n        IF OBJECT_ID(''dbo.' + @tableName + ''', ''U'') IS NOT NULL\n        BEGIN\n            DROP TABLE dbo.' + @tableName + '\n        END\n\n        SELECT * INTO dbo.' + @tableName + '\n        FROM [linkedserver].[database].dbo.' + @tableName + '\n    '\n    \n    BEGIN TRY\n        EXEC sp_executesql @sql\n        PRINT 'Table ' + @tableName + ' copied successfully.'\n    END TRY\n    BEGIN CATCH\n        PRINT 'Error occurred while processing table ' + @tableName + ': ' + ERROR_MESSAGE()\n    END CATCH\n\n    FETCH NEXT FROM table_cursor INTO @tableName\nEND\n\nCLOSE table_cursor\nDEALLOCATE table_cursor