DECLARE @Databases TABLE (DatabaseName NVARCHAR(255))\nINSERT INTO @Databases (DatabaseName)\nVALUES ('Database1'), ('Database2'), ('Database3') -- Add your database names here\n\nDECLARE @DatabaseName NVARCHAR(255)\nDECLARE @SQL NVARCHAR(MAX)\n\nDECLARE db_cursor CURSOR FOR\nSELECT DatabaseName\nFROM @Databases\n\nOPEN db_cursor\nFETCH NEXT FROM db_cursor INTO @DatabaseName\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    SET @SQL = 'USE ' + QUOTENAME(@DatabaseName) + '; \n                -- Place your SQL script here\n                SELECT * FROM YourTable;' -- Example script\n\n    EXEC sp_executesql @SQL\n\n    FETCH NEXT FROM db_cursor INTO @DatabaseName\nEND\n\nCLOSE db_cursor\nDEALLOCATE db_cursor