DECLARE @insert NVARCHAR(MAX)\nDECLARE @table NVARCHAR(128) = 'YourTableName'\nDECLARE @message NVARCHAR(MAX) = 'YourMessage'\n\n-- Escape single quotes in @message\nSET @message = REPLACE(@message, '''', '''''')\n\n-- Use QUOTENAME to safely handle the table name\nSET @table = QUOTENAME(@table)\n\n-- Construct the query\nSET @insert = 'INSERT INTO ' + @table + ' (YourColumnName) VALUES (N''' + @message + ''')'\n\n-- Execute the query\nEXEC sp_executesql @insert