WITH SequencedLetters AS (\n    SELECT \n        id,\n        [Date],\n        Letter,\n        CHAR(ASCII(Letter) + 1) AS NextLetter\n    FROM \n        YourTable\n)\nSELECT \n    t1.id, \n    t1.[Date], \n    t1.Letter AS CurrentLetter, \n    t1.NextLetter AS ExpectedNextLetter\nFROM \n    SequencedLetters t1\nLEFT OUTER JOIN \n    YourTable t2 \n    ON t1.NextLetter = t2.Letter AND t1.[Date] = t2.[Date]\nWHERE \n    t2.id IS NULL\nORDER BY \n    t1.id;