-- Example using SQL Server Audit\n    CREATE SERVER AUDIT AuditEvents\n    TO FILE (FILEPATH = 'C:\AuditLogs\', MAXSIZE = 10 MB);\n\n    CREATE DATABASE AUDIT SPECIFICATION AuditSpec\n    FOR SERVER AUDIT AuditEvents\n    ADD (INSERT, UPDATE, DELETE ON dbo.Events BY [dbo]);\n\n    ALTER SERVER AUDIT AuditEvents WITH (STATE = ON);\n    ALTER DATABASE AUDIT SPECIFICATION AuditSpec WITH (STATE = ON);