-- First select statement\nWITH FirstSelect AS (\n    SELECT 'val1' AS value UNION ALL\n    SELECT 'val3' UNION ALL\n    SELECT 'val5' UNION ALL\n    SELECT 'val2' UNION ALL\n    SELECT 'val4'\n),\n\n-- Second select statement\nSecondSelect AS (\n    SELECT 'val1' AS col1, 'data1' AS col2 UNION ALL\n    SELECT 'val2', 'data2' UNION ALL\n    SELECT 'val3', 'data3' UNION ALL\n    SELECT 'val4', 'data4' UNION ALL\n    SELECT 'val5', 'data5'\n)\n\n-- Sorting the second select statement based on the order from the first select statement\nSELECT S.*\nFROM SecondSelect S\nJOIN FirstSelect F ON S.col1 = F.value\nORDER BY CASE S.col1\n    WHEN 'val1' THEN 1\n    WHEN 'val3' THEN 2\n    WHEN 'val5' THEN 3\n    WHEN 'val2' THEN 4\n    WHEN 'val4' THEN 5\nEND;