-- Create a database master key\n   CREATE MASTER KEY ENCRYPTION BY PASSWORD = 'your_master_key_password';\n\n   -- Create a certificate protected by the master key\n   CREATE CERTIFICATE MyCertificate WITH SUBJECT = 'My Encryption Certificate';\n\n   -- Create a symmetric key protected by the certificate\n   CREATE SYMMETRIC KEY MySymmetricKey WITH ALGORITHM = AES_256 ENCRYPTION BY CERTIFICATE MyCertificate;\n\n   -- Encrypt data\n   OPEN SYMMETRIC KEY MySymmetricKey DECRYPTION BY CERTIFICATE MyCertificate;\n   UPDATE YourTable SET EncryptedColumn = EncryptByKey(Key_GUID('MySymmetricKey'), 'Sensitive Data');\n   CLOSE SYMMETRIC KEY MySymmetricKey;\n\n   -- Decrypt data\n   OPEN SYMMETRIC KEY MySymmetricKey DECRYPTION BY CERTIFICATE MyCertificate;\n   SELECT CONVERT(varchar, DecryptByKey(EncryptedColumn)) AS DecryptedData FROM YourTable;\n   CLOSE SYMMETRIC KEY MySymmetricKey;