DECLARE @xml_data xml;\n   SET @xml_data = '<your XML content>';\n   \n   -- Example of querying the XML directly without using OpenXML\n   SELECT\n       T.c.value('(tagName)[1]', 'datatype') AS ColumnName\n   FROM\n       @xml_data.nodes('/root/element') T(c);\n\n   -- If OpenXML is necessary\n   DECLARE @idoc int;\n   \n   EXEC sp_xml_preparedocument @idoc OUTPUT, @xml_data;\n   \n   -- Your OPENXML query here\n   \n   EXEC sp_xml_removedocument @idoc;