DECLARE\n    v_sql_clob CLOB;\n    v_sql      VARCHAR2(4000);\n    CURSOR c_tables IS\n        SELECT table_name FROM user_tables WHERE table_name IN ('TABLE1', 'TABLE2'); -- Specify your table names here\n    CURSOR c_data(p_table_name VARCHAR2) IS\n        SELECT * FROM (SELECT * FROM p_table_name);\nBEGIN\n    FOR r_table IN c_tables LOOP\n        DBMS_LOB.createtemporary(v_sql_clob, TRUE);\n        FOR r_data IN c_data(r_table.table_name) LOOP\n            v_sql := 'INSERT INTO ' || r_table.table_name || ' VALUES (';\n            FOR i IN 1..r_data%ROWTYPE.COUNT LOOP\n                v_sql := v_sql || CASE\n                                  WHEN r_data(i) IS NULL THEN 'NULL'\n                                  WHEN r_data(i) IS NOT NULL AND r_data(i) LIKE '%\''%' THEN 'q''[' || r_data(i) || ']'''\n                                  ELSE '''' || r_data(i) || ''''\n                                  END || ', ';\n            END LOOP;\n            v_sql := RTRIM(v_sql, ', ') || ');' || CHR(10);\n            DBMS_LOB.writeappend(v_sql_clob, LENGTH(v_sql), v_sql);\n        END LOOP;\n\n        -- Write the CLOB to a file\n        UTL_FILE.put_line(UTL_FILE.fopen('YOUR_DIRECTORY', r_table.table_name || '_data.sql', 'W'), v_sql_clob);\n        DBMS_LOB.freetemporary(v_sql_clob);\n    END LOOP;\nEND;\n/\n\n-- Note: Replace 'YOUR_DIRECTORY' with the directory object where you want to save the files.\n-- You need to create the directory object beforehand:\n-- CREATE DIRECTORY YOUR_DIRECTORY AS '/path/to/your/directory';