BEGIN TRY\n        -- Log start of procedure\n        INSERT INTO ProcedureLog (ProcedureName, StartTime) \n        VALUES ('YourProcedureName', GETDATE());\n\n        -- Procedure code here\n\n        -- Log successful completion\n        UPDATE ProcedureLog\n        SET EndTime = GETDATE(), Status = 'Success'\n        WHERE ProcedureName = 'YourProcedureName' AND EndTime IS NULL;\n    END TRY\n    BEGIN CATCH\n        -- Log error\n        INSERT INTO ErrorLog (ProcedureName, ErrorMessage, ErrorTime)\n        VALUES ('YourProcedureName', ERROR_MESSAGE(), GETDATE());\n        \n        THROW;\n    END CATCH