SELECT \n    m.date AS MDate, \n    s.date AS SDate, \n    s.symbol, \n    s.open, \n    s.high, \n    s.low, \n    s.close, \n    s.volume\nFROM \n    Masterdates m\nLEFT JOIN \n    Stockdata s\nON \n    m.date = s.date AND s.symbol = 'Foo'\nWHERE \n    m.date BETWEEN \n        (SELECT MIN(date) FROM Stockdata WHERE symbol = 'Foo')\n    AND \n        (SELECT MAX(date) FROM Stockdata WHERE symbol = 'Foo')\nORDER BY \n    m.date;